def reverse(s):    return s[::-1]def isPalindrome(s):    # Calling reverse function    rev = reverse(s)    # Checking if both string are equal or not    if (s == rev):        return True    return False# Driver codes = "madam"ans = isPalindrome(s)if ans == 1:    print("Yes")else:    print("No")def test_1():    assert isPalindrome("madam")def test_2():    assert not isPalindrome('noor')def test_3():    assert  isPalindrome('123321')def test_4():    assert not isPalindrome('123abc')